#include <iostream> using namespace std; struct Funcionario//Montando struct encadeada { 	int prontuario; 	string nome; 	double salario; 	struct Funcionario *prox; }; Funcionario* init() //Iniciar lista encadeada com valor null { 	return NULL; } Funcionario* insert(Funcionario* lista, int i)//Encadear o num prontuario { 	Funcionario* novo=new Funcionario; 	novo->prontuario=i; 	novo->prox=lista; 	return novo; } Funcionario* remove(Funcionario* lista, int i)//Desencadear um prontuario { 	Funcionario *ant = NULL; 	Funcionario *aux; 	 	aux = lista; 	while (aux != NULL && aux->prontuario != i) 	{ 		ant = aux; 		aux = aux->prox; 	} 	if (aux == NULL) 	{ 		return lista; 	} 	if (ant == NULL) // era primeiro 	{ 		lista = aux->prox; 	} 	else // estava no meio 	{ 		ant->prox = aux->prox; 	} 	free(aux); 	return lista; } void print(Funcionario* lista)//Exibir ficha dos funcionarios + salario total { 	Funcionario* aux; 	aux = lista; 	int salario=0; 	while (aux != NULL) 	{ 		cout << "Numero prontuario: " << aux->prontuario <<" - Nome: "<<aux->nome<<" - Salario: R$"<<aux->salario<<endl; 		salario=salario+aux->salario; 		aux = aux->prox; 	} 	cout<<endl<<"Total dos salarios: R$"<<salario<<endl; } Funcionario* find(Funcionario* lista, int i)//Procurar prontuario { 	Funcionario* aux; 	aux = lista; 	while (aux != NULL && aux->prontuario != i) 	{ 		aux = aux->prox; 	} 	if (aux == NULL) 	{ 		aux = new Funcionario; 		aux->prontuario = -1; 		aux->prox = NULL; 	} 	return aux; } 
